mapper.select:

SELECT * FROM CARPOOL
 WHERE saddr like #{saddr} and eaddr like #{eaddr}
 ORDER BY no DESC

SELECT * FROM carpool
WHERE subject like #{search}
ORDER BY no DESC

SELECT no, email, name, phone, subject, content FROM ATTEND
WHERE name like #{search}
ORDER BY no DESC

SELECT email, no
		FROM ATTEND
		where email=#{email} and no=#{no}

SELECT * FROM CARPOOL 
where email = #{email}
ORDER BY no DESC

SELECT NO FROM CARPOOL
where NO = #{no}
ORDER BY no DESC
